import sign from "./sign";

function buildTransactionString(sender, receiver, amount, former, refered) {
  return receiver + amount;
}

export default function(amount, receiver, wallet) {
  const sig = sign(receiver + amount, wallet);
  console.log("Create Transaction", amount, receiver, sig);
}
