import React, { useState, useMemo } from "react";
import useLocalStorage from "./hooks/useLocalStorage";
import {
  createWallet,
  calculateBalance,
  updateWalletTransactions,
  copyToClipboard,
  createTransaction
} from "./services";
import { postTransaction } from "./services/blockchain";
import "./App.css";
import { Button, Form, TextArea, Input } from "semantic-ui-react";

function App() {
  const [wallet, setWallet] = useLocalStorage("wallet");
  const [loadingTransactions, setLoadingTransactions] = useState(false);
  const [endpoint, setEndpoint] = useState("http://localhost:5000");
  const [amount, setAmount] = useState("");
  const [receiver, setReceiver] = useState("");

  return (
    <div className="clbc-container">
      <h1>Consensusless Blockchain</h1>
      {wallet ? (
        <div>
          <div className="clbc-wallet-container">
            <div>
              <div className="clbc-balance-wrapper">
                <h3>Balance</h3>
                <p>{calculateBalance(wallet)}</p>
              </div>
              <div className="clbc-public-key-wrapper">
                <h3>Public Key</h3>
                <p>
                  {wallet.pem_pub_key.split("\n").map(p => (
                    <>
                      {p}
                      <br />
                    </>
                  ))}
                </p>
              </div>
            </div>
            <div>
              <div className="clbc-create-transaction-wrapper">
                <h3>Send</h3>
                <Form>
                  <Form.Field>
                    <label>Amount</label>
                    <input
                      placeholder="Amount"
                      value={amount}
                      onChange={e => setAmount(e.target.value)}
                    />
                  </Form.Field>
                  <Form.Field>
                    <label>Receiver Key</label>
                    <TextArea
                      placeholder="Receiver Key"
                      value={receiver}
                      onChange={e => {
                        console.log(e.target.value);
                        setReceiver(e.target.value);
                      }}
                    />
                  </Form.Field>
                  <Button
                    type="button"
                    primary
                    onClick={async () =>
                      postTransaction(
                        endpoint,
                        await createTransaction(amount, receiver, wallet)
                      )
                    }
                  >
                    Submit
                  </Button>
                </Form>
              </div>
              <div className="clbc-create-transaction-history-wrapper">
                <h3>History</h3>
                <Button
                  primary
                  onClick={async () => {
                    setLoadingTransactions(true);
                    setWallet(await updateWalletTransactions(endpoint, wallet));
                    setLoadingTransactions(false);
                  }}
                >
                  Reload
                </Button>
                <ul>
                  {wallet.transactions.map(t => (
                    <li onClick={() => copyToClipboard(t.sig)}>
                      {t.sig.slice(0, 30) + "..."}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="clbc-no-wallet-container">
          <h2>>No wallet found</h2>
          <p>To interact with the blockchain you need to create a wallet.</p>
          <Button primary onClick={async () => setWallet(await createWallet())}>
            Create New Wallet
          </Button>
        </div>
      )}
      <div>
        <h2>Use Node</h2>
        <Input value={endpoint} onChange={e => setEndpoint(e.target.value)} />
      </div>
    </div>
  );
}

export default App;
