import getOpenTransactionBalance from "./getOpenTransactionBalance";

export default function findFormer(wallet, amount) {
  const transactions = Object.keys(wallet.transactions);
  let rest = amount;
  let former = {};
  for (let t in transactions) {
    const openTransactionBalance = getOpenTransactionBalance(
      Object.values(wallet.transactions),
      t
    );
    if (openTransactionBalance > 0) {
      let val = Math.min(amount, openTransactionBalance);
      former[t] = rest -= val;
    }
    if (rest <= 0) {
      return former;
    }
  }
  return null;
}
