function getLastOpenTransaction(wallet) {
  for (let t in Object.values(wallet.transactions)) {
    if (!t.isValid) {
      return t;
    }
  }
}

export default function getReferenceTransactions(wallet, openTransaction) {
  const reference = [];
  const lot = getLastOpenTransaction(wallet);
  if (lot) {
    reference.push(lot.sig);
  }
  if (openTransaction) {
    reference[openTransaction.sig] = openTransaction;
  }
  return reference;
}
