import rsa from "js-crypto-rsa"; // for npm
import { str2ab } from "../strAb";
export default function(transaction, wallet) {
  console.log(transaction, wallet.privateKey);
  rsa
    .sign(str2ab(transaction), wallet.privateKey, "SHA-256", {
      // optional
      name: "RSA-PSS", // default. 'RSASSA-PKCS1-v1_5' is also available.
      saltLength: 64
    })
    .then(signature => {
      console.log(signature);
    });
}
