import cryptico from "cryptico";
import { transactions as mock_transaction } from "../../mock";
import rsa from "js-crypto-rsa";
import keyutils from "js-crypto-key-utils";

export default async function createWallet() {
  var passPhrase = "The Moon is a Harsh Mistress.";
  var Bits = 2048;
  // var rsa_key = cryptico.generateRSAKey(passPhrase, Bits);
  const key = new keyutils.Key("jwk", await rsa.generateKey(2048));
  const pem = await key.export("pem", { outputPublic: true, compact: true });
  console.log(pem);
  console.log(key.publicKey, key.privateKey);
  return {
    publicKey: key.publicKey,
    transactions: mock_transaction,
    privateKey: key.privateKey
  };
}
